              <option value="200" ${savedState.mainPanelSize === '200' ? 'selected' : ''}>200A</option>
              <option value="400" ${savedState.mainPanelSize === '400' ? 'selected' : ''}>400A</option>
              <option value="600" ${savedState.mainPanelSize === '600' ? 'selected' : ''}>600A</option>
