    'stone': { 'straight': 10, 'diagonal': 18, 'herringbone': 20, 'brick': 12 }
  };

  const baseWaste = baseWasteFactors[flooringType]?.[patternLayout] || 
                   baseWasteFactors[flooringType]?.['straight'] || 8;

