  content.innerHTML = `
    <div class="results-grid">
      <div class="result-section">
        <h4>Area & Material Calculations</h4>
        <div class="result-item">
          <span class="label">Base Area:</span>
          <span class="value">${formatNumber(results.baseArea, 1)} sq ft</span>
