      },
      zoneControls: 450,
      averageAccessory: 35,
      laborMultiplier: 2.2
    },
    'northeast': {
      equipment: {
        'split-system': { 2: 4200, 3: 5000, 4: 6100, 5: 7400 },
        'package-unit': { 2: 5000, 3: 6000, 4: 7300, 5: 8800 },
        'vrf': { 2: 10200, 3: 13200, 4: 16800, 5: 21000 },
        'chiller': { 2: 14400, 3: 19200, 4: 24000, 5: 30000 },
        'gas-furnace': { 2: 3400, 3: 4100, 4: 5000, 5: 6100 }
      },
      ductwork: {
