              <option value="hot-only" ${savedState.pipeInsulation === 'hot-only' ? 'selected' : ''}>Hot Water Only</option>
              <option value="all-pipes" ${savedState.pipeInsulation === 'all-pipes' ? 'selected' : ''}>All Supply Lines</option>
